import React from "react";
import "./TodoPage.css";
function TodoPage() {
  const handleFormat = (format) => {
    document.execCommand(format, false, null);
  };
  return (
    <div className="todo-container">
      <div className="todo-title">
        <input placeholder="Title" className="title-input1" />
      </div>
      <br />
      <div className="todo-body">
        <div className="text-change">
          <span className="text-format" onClick={() => handleFormat("bold")}>
            B
          </span>
          <span className="text-format" onClick={() => handleFormat("Italic")}>
            I
          </span>
          <span className="text-format" onClick={() => handleFormat("Normal")}>
            Norma;
          </span>
        </div>
        <textarea placeholder="Enter here ..." className="body-input" />
      </div>
    </div>
  );
}

export default TodoPage;
