import React, { useEffect, useState } from "react";
import ReactQuill from "react-quill";
import "react-quill/dist/quill.snow.css";
import "./TodoPage.css";
// import jwt from "jsonwebtoken";
import jwt_decode from "jwt-decode";
import axios from "axios";
import { useParams } from "react-router";
function TodoPage() {
  const { taskId } = useParams();
  console.log(taskId + " needed");
  const [title, settitle] = useState("");
  const [text, setText] = useState("");
  const getToken = () => {
    return localStorage.getItem("token");
  };
  const handleTextChange = (newText) => {
    setText(newText);
    console.log(text); // Update the text state with the new content
  };

  const CreateTask = async () => {
    try {
      const token = getToken();
      console.log(token + " this is token");
      const decodedtoken = jwt_decode(token);
      const userId = decodedtoken.id;
      const response = await axios.post("http://localhost:3000/api/tasks", {
        title,
        task: text,
        userId,
      });
      console.log("Task create ", response.data);
    } catch (error) {
      console.log("error");
    }
  };
  const fetchTask = async () => {
    try {
      const token = getToken();
      console.log(token + " this is token");
      const decodedtoken = jwt_decode(token);
      const userId = decodedtoken.id;
      console.log(userId);
      const response = await axios.get("http://localhost:3000/api/tasks", {
        headers: {
          Authorization: token,
        },
        params: { userId },
      });

      if (response.data.length > 0) {
        const fetchedData = response.data[0];
        settitle(fetchedData.title);
        handleTextChange(fetchedData.task);
      }

      console.log(response.data[0].title);
    } catch (error) {
      console.log("error");
    }
  };
  useEffect(() => {
    fetchTask();
  }, []);

  window.addEventListener("beforeunload", async (event) => {
    try {
      const token = getToken();
      const decodedToken = jwt_decode(token);
      const userId = decodedToken.id;

      await axios.put(
        `http://localhost:3000/api/tasks/todo/${taskId}/edit`,
        {
          title,
          task: text,
          userId,
        },
        {
          headers: {
            Authorization: token,
          },
        }
      );

      // Since the actual save process might not complete,
      // you can show a message here to indicate the attempt.
      event.returnValue = "Your changes are being saved.";
    } catch (error) {
      // Handle the error, and potentially show an error message.
      console.log(error);
    }
  });

  return (
    <div className="todo-container">
      <div className="todo-title">
        <input
          placeholder="Title"
          value={title}
          onChange={(e) => settitle(e.target.value)}
          className="title-input1"
        />
      </div>
      <br />
      <div className="todo-body">
        <ReactQuill
          value={text}
          onChange={handleTextChange}
          className="body-input"
          placeholder="Enter here ..."
        />
      </div>
      <button onClick={CreateTask}>but</button>
      {/* <button onClick={UpdateTask}>but</button> */}
    </div>
  );
}

export default TodoPage;
